import './App.css';
import React, { FC, useState, useEffect, useContext, useCallback } from 'react';
import TodoItem from '../TodoItem/TodoItem'
import axios from 'axios'
import AddButton from '../AddButton/AddButton'
import { ITodo } from '../../interfaces/todo';
import { ApiContext } from '../../contexts/ApiContext'

const App: FC = () => {
  const [gotData, setGotData] = useState<boolean>(false)
  const [items, setItems] = useState<ITodo[] | undefined>(undefined)
  const { getTodoData } = useContext(ApiContext)

  const getData = useCallback(async () => {
    const data = await getTodoData()

    setItems(data)
  }, [])

  useEffect(() => {
    if (!gotData) {
      getData()
      setGotData(true)
    }
  }, [getData, gotData])


  return (
    <div className="App">
      <header className="App-header">
        <h1>Todo</h1>
        <AddButton getData={getData}/>
      </header>
      <main className="App-main">
        {items?.map((value, index) => (
          <TodoItem key={index} title={value.title} completed={value.completed} />
        ))}
      </main>
    </div>
  );
}

export default App;
